mapper.select:
SELECT
-- 级联表的字段 ,jingdian.jingdian_name as jingdianName ,jingdian.jingdian_types as jingdianTypes ,jingdian.jingdian_dengji_types as jingdianDengjiTypes ,jingdian.jingdian_photo as jingdianPhoto ,jingdian.jingdian_new_money as jingdianNewMoney ,jingdian.jingdian_content as jingdianContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto
FROM jingdian_order a left JOIN jingdian jingdian ON a.jingdian_id = jingdian.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.jingdian_id = #{params.jingdianId}
and a.yonghu_id = #{params.yonghuId}
and a.jingdian_number >= #{params.jingdianNumberStart}
and a.jingdian_number <= #{params.jingdianNumberEnd}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and jingdian.jingdian_name like CONCAT('%',#{params.jingdianName},'%')
and jingdian.jingdian_types = #{params.jingdianTypes}
and jingdian.jingdian_dengji_types = #{params.jingdianDengjiTypes}
and jingdian.jingdian_new_money >= #{params.jingdianNewMoneyStart}
and jingdian.jingdian_new_money <= #{params.jingdianNewMoneyEnd}
and jingdian.jingdian_content like CONCAT('%',#{params.jingdianContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
order by a.${params.orderBy} desc
